mapper.update:
UPDATE STORE
SET STORE_WEIGHT = STORE_WEIGHT + 1
WHERE STORE_CODE = #{storeCode}

UPDATE STORE
SET STORE_WEIGHT = STORE_WEIGHT - 1
WHERE STORE_CODE = #{storeCode}

UPDATE STORE
SET STORE_WEIGHT_COUNT = STORE_WEIGHT_COUNT + 1
WHERE STORE_CODE = #{storeCode}

UPDATE STORE
SET STORE_WEIGHT_COUNT = STORE_WEIGHT_COUNT - 1
WHERE STORE_CODE = #{storeCode}
